mapper.select:
SELECT a.*, r.sido_name FROM acc a LEFT JOIN region r ON a.region_id = r.region_id WHERE a.deleted_at IS NULL
AND a.address LIKE CONCAT('%', #{region}, '%')
AND a.type_code_code = #{code}
AND a.type_code_id = #{codeTypeId}
AND (a.name LIKE CONCAT('%', #{keyword}, '%') OR a.address LIKE CONCAT('%', #{keyword}, '%') OR a.acc_intro LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY a.acc_id DESC

mapper.update:
UPDATE acc
SET deleted_at = NOW()
WHERE acc_id = #{accId}
